vec2 uv1 = vec2(uv.x-0.5,uv.y-0.5)*aspect.xy;

vec2 tmp = uv1;
tmp.x = ang*3; tmp.y = rad;
uv1 = tmp;

vec3 noise = (texture(sampler_noise_lq,corr*uv/2+q23).xyz+1);

vec3 ret1 = ((texture(sampler_blur1, uv).xyz * scale1) + bias1)-.3;
vec3 crisp = texture(sampler_main, uv+ret1*.01 ).xyz + noise*0.1;
vec3 blur = ((texture(sampler_blur1, uv/4+.4*vec2(.3*ret1.x,ret1.y)).xyz * scale1) + bias1) - noise*.1;

