frame_eqs_eel:
dt = min (1/fps,0.1) ;
dec_m = 1 - 4*dt; dec_s = 1 - dt;
vol = (bass+mid+treb)/3 ;
vol_=  vol_*dec_m + (1-dec_m)*vol; 
exc = (vol-vol_)*1;

index=0;
loop (num_res,
  n = index*8;
  w = megabuf(n) * 6.2832/60;
  megabuf (n+1) *= 1-0.32*dt; //damping
  ec = int (8*w*dt)+1; 
  loop (ec,
    megabuf(n+1) += (exc-sqr(w)*megabuf(n+2)) * dt/ec;
    megabuf(n+2) += megabuf(n+1) * dt/ec);
  bc = megabuf(n+1);  
  bs = megabuf(n+2)*w;
  megabuf (n+3) = megabuf(n+3) *dec_s + quad* (1-dec_s);
  megabuf (n+4) = megabuf(n+3);
  gmegabuf (n+5) = bc*.11/(1+omaxq)*sqrt(vol_);
  gmegabuf (n+6) = bs*.11/(1+omaxq)*sqrt(vol_);
  index += 1;
);
trel += BPM/60*5.84338*dt;
q24 = min(1,max(0,1+2*sin(trel/64)));
q23 = min(1,max(0,3*sin((t1+t2)*5)-.5)*(1-q24));
n = 0; maxind = 0; 
