warp:
shader_body
{
    vec2 v = normalize(uv-uv_orig);
    uv = uv_orig + v*texsize.zw*5;

    // sample previous frame
    ret = texture( sampler_main, uv ).xyz;

    // darken over time
    ret *= 0.97; //or try: ret -= 0.0269;
}

comp:
shader_body
{
    uv = 0.99*uv;
    ret = texture(sampler_main, uv).xyz;
      // SUPER GLOW EDGES - looks awesome w/octopus
      vec3 avg_col = ((texture(sampler_blur1, uv).xyz * scale1) + bias1);
      //ret = abs(avg_col - ret)*8;
    ret *= 1.333; // a little bit of overbright
}

