warp:
shader_body
{
    float t = clamp((0.9)*3, 0.0, 1.0);
    uv = mix(uv_orig, uv, t*sin(ang+time)*0.9);

    vec2 v1 = normalize(uv_orig-0.5);
    vec2 v2 = v1.yx * vec2(1,-1);

    float z = length(texsize.zw)*450;

    uv.xy += v1*texsize.zw * cos(rad*170 )/2;
    uv.xy += v2*texsize.zw * cos(ang*170 )/2;

    uv += texture(sampler_noise_hq, rand_frame.xy + uv*16*texsize.xy*texsize_noise_hq.zw).xyz*texsize.zw*6;

    // sample previous frame
    ret = texture( sampler_main, uv ).xyz;



    // darken (decay) over time
    ret = (ret - 0.004)*2.83;
