warp:
vec2 uv1, uv2, zz, h1, noise;

vec3 GP (vec2 uvi, float rat)
{return mix (texture(sampler_main, uvi).xyz,((texture(sampler_blur3, uvi).xyz * scale3) + bias3),rat);}

vec3 GP1 (vec2 uvi, float rat)
{return mix (((texture(sampler_blur1, uvi).xyz * scale1) + bias1),((texture(sampler_blur1, uvi).xyz * scale1) + bias1),rat);}

shader_body {
float fea=rand_preset.y>0.3; 
uv1 = uv-0.5;
uv2 = vec2 (rad,ang/1.56);
uv1= mix(uv1,uv2,rand_preset.x);
zz = uv1 * q27 ;

float a= M_PI*rad*q28+q29;
zz =(mat2(sin(a),cos(a),-cos(a), sin(a)) * zz);
h1 = clamp(tan(zz),-12,12);
uv2 = uv+h1 * .1/q27;

ret= GP1(uv2,0.5);
ret= max(ret,GP((uv2+uv_orig)/2,0.5)*fea);

ret = ret*(0.84-fea*0.1)-0.005*(frame%2);
ret -=.7*pow(lum(((texture(sampler_blur2, uv_orig).xyz * scale2) + bias2)),2);

//ret = 0;

}

comp:
vec3 ret1, neu, blur, back;
vec2 uv1,uv2,uv3,uv4;

shader_body 
{
uv1 = (uv-.5)* aspect.xy;    
ret1=((texture(sampler_blur2, .5+rad*.15).xyz * scale2) + bias2)/2; blur=0;

int anz=3;
for (int n = 0; n < anz; n++)
{
    float ang2 = 1.0*n/anz*M_PI;
    uv2 = (mat2(sin(ang2),cos(ang2),-cos(ang2),sin(ang2)) * uv1);
    uv2 *= aspect.yx*0.85/min(aspect.x,aspect.y);
    uv3=fract(abs(fract(uv2)-.5));
    neu = texture(sampler_main, uv3).xyz+((texture(sampler_blur1, uv3).xyz * scale1) + bias1) + ((texture(sampler_blur2, uv3).xyz * scale2) + bias2)/3;;
    ret1 = max(ret1,neu);
    blur = max(blur,((texture(sampler_blur2, uv3).xyz * scale2) + bias2))*1.25;
}

ret = mix(lum(blur),ret1,q15);
ret = ret*1/(rad*2+0.5);
ret = 1-exp(-ret*2.2);
//ret = texture(sampler_main, uv).xyz;



}

