// by LamersAss, V1 (28/5/2022)

shader_body
{
  vec3 _fx_vol = vec3( bass_att, mid_att, treb_att )/6;

  for ( int i = 0; i < 3; i++ )
  {
    _fx_vol[i] = ( min( 1., _fx_vol[i] ) * 1.7 )
                 + max( .0, _fx_vol[i] - 1. );
    _fx_vol[i] = min( 2.46, _fx_vol[i] );
  }

  _fx_vol += rot_f3[0] * .1;
  _fx_vol *= .07;
  _fx_vol *= 1.9;
  _fx_vol += 0.022;

  float _fx_decay = fps * 0.015786;
  float _fx_rad = length( vec2( 
    ( uv.x - .5 ) * aspect.x, 
    ( uv.y - .5 ) * aspect.y ) );
  float _fx_zoom = max( 0, _fx_rad - .12 ) * .028;

  _fx_zoom *= 1. + max( 0, ( cos( ang * 4 ) * .7 ) - .4 );

  vec2 _fx_prevframe = uv_orig;
  _fx_prevframe.x += ( _fx_zoom * aspect.y ) * ( uv_orig.x > .5 ? -1. : 1. );
  _fx_prevframe.y += ( _fx_zoom * aspect.x ) * ( uv_orig.y > .5 ? -1. : 1. );

  ret = texture( sampler_main, _fx_prevframe ).xyz * _fx_decay;

  float _fx_width = ( _fx_rad * .04 ) + ( vol * .004 );
  float _fx_spinmult = 600.;

  for ( int i = 0; i < 3; i++ )
    if ( _fx_rad > ( _fx_vol[i] - _fx_width ) 
      && _fx_rad < ( _fx_vol[i] + _fx_width ) )
      ret[i] = max( ret[i],
        ( 12. * _fx_vol[i] ) *
        + ( 12. * cos( ang + ( _fx_spinmult * _fx_vol[i] ) ) )
     );

  vec3 _fx_glow_intens = vec3(-2.7,0.3,2.5);
  vec3 _fx_glow_vol = ( _fx_vol * 0.92 ) * max( 0, _fx_rad - .2 );

  for ( int i = 0; i < 3; i++ )
    ret[i] = max( ret[i], _fx_glow_vol[i] * _fx_glow_intens[i] );
}

