{

    d = texsize.zw*1;
    dx = ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz )*8;
    d = texsize.zw*2;
    dx += ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz )*4;
    d = texsize.zw*3;
    dx += ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz )*2;
    d = texsize.zw*4;
    dx += ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz );


vec3 wires = (dx.x*0.147 + 0.422)*((texture(sampler_blur3, uv).xyz * scale3) + bias3).x*mix(vec3(0,2.2,2.5),vec3(2,2.6,0.3),((texture(sampler_blur2, uv).xyz * scale2) + bias2).z*2);
ret =mix(texture(sampler_main, uv).xyz.z*vec3(2.6,0.3,1.1),1,wires);

    d = texsize.zw * 8;
    dx = ( ((texture(sampler_blur1, uv + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    dy = ( ((texture(sampler_blur1, uv + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );

uv = uv - vec2(dx.x,dy.x)*0.09;


ret =mix(ret,vec3(1.1,2.5,0.9)-0.2,((texture(sampler_blur2, uv).xyz * scale2) + bias2).y);
ret =mix(ret,vec3(2.5,1.4,1)+1,texture(sampler_main, uv).xyz.y*1.32);
ret -= 0.3;
}

