comp:
// (MWmod) uniform sampler2D sampler_rand00;
float cross, noise, z, mask, scale;
vec2 rs, rs0, uv1, uv2, uv3, uv4, uvt;
vec3 ret1,ret2,struc,flash2;

vec3 Get1 (vec2 uvi){return texture(sampler_main, uvi).xyz;}

float _pow(float b,float e){return pow(b,e);}
vec2  _pow(vec2  b,float e){return pow(b,vec2(e));}
vec3  _pow(vec3  b,float e){return pow(b,vec3(e));}
vec4  _pow(vec4  b,float e){return pow(b,vec4(e));}
shader_body {

float iter = 4+10*clamp(texsize.x/1024*uv.y, 0.0, 1.0);
ret =Get1(uv); ret1 = ret; ret2 = ret;
for (int n =1; n < 10; n++) {
  ret1 = max(ret1,Get1(uv+texsize.zw*vec2(n,0)));
//  ret2 = max(ret2,Get1(uv+texsize.zw*vec2(0,n)));
}
struc = ret1;
mask = clamp(1-16*lum(struc), 0.0, 1.0);
struc *= .06/(length(rad/2-length((ret1+ret2*0.2))+0.6));

//BACK
//uv4 = (uv-0.5) * (1+0*exp(-_pow((uv.x-0.5)*3-2*q14,2)))+0.5;


noise = (texture(sampler_noise_lq,2*uv4).r+0.25);
noise *= 1-texture(sampler_noise_lq,2*uv4+texsize.zw*-4).r;
// (MWmod) noise *= (0.5+ 0.5*(lum(texture(sampler_rand00,uv).xyz) > 0.5))*2;
ret =0.05+noise*0.5*(1-rad);  // texLum (orig *0.5)

ret *= clamp(1-lum(((texture(sampler_blur2, uv4-0.13).xyz * scale2) + bias2))*8, 0.0, 1.0);
ret =mix (ret,struc, !mask);

//NEW FLASH
for (int n = 1; n <= 3; n++)
{
  uvt = texture(sampler_noise_lq,n*0.074+q25).xy;
  uv3 = uv4-uvt;
  uv3 = (mat2 (q23,q24,-q24,q23) * uv3);
  cross = (0.0001+abs (uv3.x+uv3.y) *abs (uv3.x-uv3.y)) * length(uv3);
  flash2 = (3*q22/cross) *((texture(sampler_blur1, uvt).xyz * scale1) + bias1) * (uvt.x < 0.9)* (uvt.x > 0.1)* (uvt.y < 0.9)* (uvt.y > 0.1);
  flash2 += 100000*mask*q22*clamp(1-2*length (uv3-0.1), 0.0, 1.0) *noise*((texture(sampler_blur1, uvt).xyz * scale1) + bias1);
  ret += flash2;
}
//ret =texture(sampler_main, uv).xyz;
//ret *= 1.4;  // gamma  (orig 1.4  und  deakt)
//ret /= vec3(1.8,-0.8,2.8);
ret /= vec3(0.6,2.6,-0.6);
ret /= vec3(1.5,1.8,-2.0);
ret /= vec3(2.0,-0.2,-2.3);
ret /= vec3(1.0,-1.5,-0.1);
}

