{
    // PAINTERLY EFFECT:
    vec2 t = 0.005 + 0.045*clamp(vec2(bass,treb)-1, 0.0, 1.0);
    vec2 uv2 = uv;
    vec2 delta2 = texsize.zw*vec2(1,1);
    vec3 blurry_color = texture( sampler_main, 
