warp:
shader_body
{
    // PAINTERLY EFFECT:
    vec2 t = 0.001 + 0.045*clamp(vec2(bass,treb)-2.3, 0.0, 1.0);
    vec2 uv2 = uv;
    vec2 delta2 = texsize.zw*vec2(12,12);
    vec3 blurry_color = texture( sampler_main, 
        mix(uv,uv_orig,-1) + delta2 ).xyz;
    vec2 v = (blurry_color.xy-0.37) * t;
      //uv2 += v;
      // instead, add a rotated version:
      uv2.x += v.x*q1 + v.y*q2;
      uv2.y += v.x*q2 - v.y*q1;

    // sample previous frame
    ret = texture( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.0035;    
}

