warp:
shader_body
{
    ret =texture(sampler_main, uv).xyz * 0.7; // Sample and decay

    vec2 z = uv;
    vec2 c;

    if(q9 == 5) c = vec2(0.285, 0);
    else if(q9 == 6) c = vec2(-0.66176 + 0.03*q20, -0.3842);
    else if(q9 == 0) c = vec2(-0.7269 + 0.01*q21, 0.1889 + 0.01*q22);
    else c = z;

    int n = 0;
    const int maxiter = 24; // Reduced from 20
    const float r = 20;
    const float r2 = r*r;

    while(n < maxiter && dot(z,z) < r2) {
        z = vec2(z.x*z.x - z.y*z.y, 2*z.x*z.y) + c;
        n++;
    }

    float smooth_iter = n - log2(log(length(z))/log(r));

    ret =vec3(
        0.5 + 0.5*tan(0.21*smooth_iter*3.5 + 0.5*time + 0.2*q5),
        0.5 + 0.5*cos(0.33*smooth_iter*3.5 + 0.7*time + 0.2*q6),
        0.5 + 0.5*tan(0.45*smooth_iter*3.5 + 0.9*time + 0.2*q7)
    );
}

comp:
float _pow(float b,float e){return pow(b,e);}
vec2  _pow(vec2  b,float e){return pow(b,vec2(e));}
vec3  _pow(vec3  b,float e){return pow(b,vec3(e));}
vec4  _pow(vec4  b,float e){return pow(b,vec4(e));}
shader_body
{



vec2 uva = uv;
vec2 uvb = vec2(1-uv.x,uv.y);

ret =vec3(texture(sampler_main, uva).x + texture(sampler_main, uvb).x);

vec3 blurs = ((texture(sampler_blur1, uva).xyz * scale1) + bias1).x + ((texture(sampler_blur3, uva).xyz * scale3) + bias3).x;
blurs += ((texture(sampler_blur1, uvb).xyz * scale1) + bias1).x + ((texture(sampler_blur3, uvb).xyz * scale3) + bias3).x;


vec2 uv3 = (uv-.5)*(1-((texture(sampler_blur1, uv).xyz * scale1) + bias1).zzz)+.5;
//ret += clamp(_pow(texture(sampler_main, uv3).zzz,.5)*(1-ret.x*2), 0.0, 1.0),
//ret += clamp(_pow(((texture(sampler_blur1, uv3).xyz * scale1) + bias1).zzz,.5)*(1-ret.x*2), 0.0, 1.0),


ret =mix (
blurs,
ret,
_pow(ret,2));
//ret *= .7;

ret =pow(ret,vec3(1.3,.7,.2));
vec3 noise9 = texture(sampler_noisevol_hq, 
                      ((uv3.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(11,1,0)*0.05 + 
                      time*vec3(0,0,1)*q31
                     ).xyz;
if(ret.x> q26*q13         && ret.x<= q23*q11        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q11         && ret.y<= q22*q12        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q12         && ret.z<= q21*q13        )ret.y -= (noise9).z*.5; 
ret += pow(texture(sampler_main, vec2(uv.x,1-uv.y)).yyy+texture(sampler_main, 1-uv).yyy,vec3(.6,.5,.5));
ret -= 1.7;

//MilkDrop3 Color Mode:
ret /= vec3(1.4,2.6,1.4);
}

