warp:
shader_body
{

    vec2 d = texsize.zw*4;
    vec3 dx = ((texture(sampler_blur1, fract(uv+vec2(1,0)*d)).xyz * scale1) + bias1)-((texture(sampler_blur1, fract(uv-vec2(1,0)*d)).xyz * scale1) + bias1);
    vec3 dy = ((texture(sampler_blur1, fract(uv+vec2(0,1)*d)).xyz * scale1) + bias1)-((texture(sampler_blur1, fract(uv-vec2(0,1)*d)).xyz * scale1) + bias1);
    vec3 b1 = ((texture(sampler_blur1, uv).xyz * scale1) + bias1) - 0;
    d *= -1;
    float d2 = d*4;
    float c1 = 0.042;
    float c2 = -0.00735;


    vec2 my_uv = uv - vec2(dx.y,dy.y)*d - vec2(dx.x,dy.x)*d2;
    ret.y = texture( sampler_fc_main, my_uv-floor(my_uv)).y;
    ret.y += (ret.y - b1.y)*c1 + c2;


    my_uv = uv - vec2(dx.x,dy.x)*d - vec2(dx.z,dy.z)*d2;
    ret.x = texture( sampler_fc_main, my_uv-floor(my_uv)).x;
    ret.x += (ret.x - b1.x)*c1 + c2;


    my_uv = uv - vec2(dx.z,dy.z)*d - vec2(dx.y,dy.y)*d2;
    ret.z = texture( sampler_fc_main, my_uv-floor(my_uv)).z;
    ret.z += (ret.z - b1.z)*c1 + c2;
//ret = 0;

}

comp:
shader_body
{
    vec2 d = texsize.zw;
    vec3 dx = ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz );
    vec3 dy = ( texture(sampler_main, uv+vec2(0,1)*d).xyz-texture(sampler_main, uv-vec2(0,1)*d).xyz );
vec2 dz = vec2(dx.y,dy.y)*33;

d = texsize.zw*2;
dx = ( 2*((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-2*((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) )*0.5;
dy = ( 2*((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-2*((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) )*0.5;

dz +=vec2(dx.y,dy.y);

vec3 bg = pow(length(dz)*0.87+0,0.7) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2).y*0.4-0.1;

ret = bg*vec3(.2,2.3,2.7); //MilkDrop2077 color mod
ret = mix(ret,vec3(0,.1,.3),texture(sampler_main, uv + vec2(dx.x,dy.x)*texsize.zw*2).xyz.x*4); //MilkDrop2077 color mod
ret = mix(ret,1,texture(sampler_main, uv).xyz.x);

