  ret1 = max(ret1,Get1(uv+texsize.zw*float2(n,0)));
//  ret2 = max(ret2,Get1(uv+texsize.zw*float2(0,n)));
}
struc = ret1;
mask = saturate(1-16*lum(struc));
struc *= .06/(length(rad/2-length((ret1+ret2*0.2))+0.6));

//BACK
//uv4 = (uv-0.5) * (1+0*exp(-pow ((uv.x-0.5)*3-2*q14,2)))+0.5;


