// (MWmod) noise *= (0.5+ 0.5*(lum(tex2D(sampler_rand00,uv)) > 0.5))*2;
ret = 0.05+noise*0.5*(1-rad);  // texLum (orig *0.5)

ret *= saturate(1-lum(GetBlur2(uv4-0.13))*8);
ret = lerp (ret,struc, !mask);

//NEW FLASH
for (int n = 1; n <= 3; n++)
{
  uvt = tex2D(sampler_noise_lq,n*0.074+q25).xy;
  uv3 = uv4-uvt;
  uv3 = mul (uv3,float2x2 (q23,q24,-q24,q23));
  cross = (0.0001+abs (uv3.x+uv3.y) *abs (uv3.x-uv3.y)) * length(uv3);
  flash2 = (3*q22/cross) *GetBlur1(uvt) * (uvt.x < 0.9)* (uvt.x > 0.1)* (uvt.y < 0.9)* (uvt.y > 0.1);
  flash2 += 100000*mask*q22*saturate(1-2*length (uv3-0.1)) *noise*GetBlur1(uvt);
  ret += flash2;
}
