wave_b = 0.5 + 0.499*( 0.60*sin(0.814*time) + 0.40*sin(1.011*time) );
wave_mystery = -0.17 + 0.03*(0.6*sin(0.637*time) + 0.4*sin(0.949*time));
mv_r = if(beat, 1, ib_r);
mv_b = if(beat, wave_b, ib_b);
//mv_a = if(beat, 0.1, ib_a);
