q2 = 1-wave_y;
q2 = ypos+0.5;
warp=0;

//q2 = 1-(ypos + 0.5);
//q1 = 0.5;
//q2=0.5;
ob_r = 1-wave_g;
ob_b = 1-wave_r;
ob_g = 1-wave_b;

monitor = wave_y;
movement =movement + 0.4*(((bass+bass_att + 0.1*pow((bass+0.6*bass_att+0.2*treb_att),3)))/fps);
movement = if(above(movement,10000), 0, movement);
rot =1*sin(movement);
cx = wave_x;
cy = y_pos+0.5;

