shapes.frame_eqs_eel:
sample=instance/num_inst;

////// dna

s1=30*sample;   s2=s1%30;
x=.5+s2-s1;     y=0;

t=.21*s2;
mx=x*sin(t)+y*cos(t);
my=y*sin(t)-x*cos(t);
mz=-2.9+.2*s2;

my_x = 1.3*mx;
my_y = 1.3*my;
my_z = mz;


rad=rad*(1+3*bnot(instance%20)+3*bnot((instance-1)%20));

//// rotations

d = 7; 
zoom =1.5;

w1 = .95*time;
w2 = 0;
w3 = 3.27;

x1 = cos(w1)*my_x + sin(w1)*my_y;
y1 = -sin(w1)*my_x + cos(w1)*my_y;
z1 = my_z;

x2 = cos(w2)*x1 + sin(w2)*z1;
z2 = -sin(w2)*x1 + cos(w2)*z1;
y2 = y1;

y3 = cos(w3)*y2 + sin(w3)*z2;
z3 = -sin(w3)*y2 + cos(w3)*z2;
x3 = x2;

l = sqrt(x3*x3 + y3*y3);
w = atan2(x3,y3);
d = sqrt(x3*x3 + y3*y3 + (z3+d)*(z3+d));
p = tan(asin(1) + atan2(d+z3,l));

my_x = zoom*sin(w)*p;
my_y = zoom*cos(w)*p;

x = 0.5 + my_x/q6;
y = 0.5 + my_y;


rad=rad*zoom/d;

