comp:
shader_body
{
    ret = texture(sampler_main, uv).xyz;
    float L = lum(ret);
    float bw = clamp(cos(L*17 + 3 + uv.x*30)*30 - 21, 0.0, 1.0);
    float L2 = lum(((texture(sampler_blur1, uv).xyz * scale1) + bias1));
    float bw2 = clamp(cos(L*17 + 3 + uv.x*30)*3 - 2.65, 0.0, 1.0);

    ret = vec3(1,1.7,2.2)*ret.x +
          vec3(2.6,2.9,0.6)*ret.y + 
          vec3(2.4,1.4,0.4)*ret.z;
    ret *= 0.4;

    ret = ret*ret * (1 + bw2*4);

ret = ret*(1-ret)*4; //MilkDrop2077 solarize effect
}

