comp:
#define sat saturate
vec3 struc;
vec2 uvc, rs, uv1, uv2,uv3, uv4, prof,prof2;
float rad1, mask, ir0, k1, omask, z;
const vec2 centre = vec2 (q10-0.5,-(q11-0.5));
const vec2 pos = vec2 (q15,q16);
const vec2 hor = texsize.zw*vec2 (1,0);
const float blue = rand_preset.y/2;;
vec3 lavcol (float t){return smoothstep(0,0.8,pow (t,vec3(1,2,4)));}

vec2 noise3 (vec2 uvi) {
  return (texture(sampler_noise_mq,uvi).xyz
         +texture(sampler_noise_mq,uvi*2).xyz/2  
         +texture(sampler_noise_mq,uvi*4).xyz/4
         +pow(texture(sampler_noise_mq,uvi*8+time*0.01).xyz,2)/4-0.8); }

vec3 project (vec2 uvi) {float z;
  z = clamp(1/uvi.y,-16,16); return vec3 (z*(uvi.x-0.5),z-2,z);}

shader_body {
uvc = (uv-0.5) * aspect.xy + 0.5 -centre;
omask = clamp(15*(0.55-uvc.y), 0.0, 1.0);
uv2 = project (uvc) * vec2(1,1.5);
vec2 ring = (texture(sampler_noise_mq,-time/16+length(uv2)/2).xyz-0.5).xy*sat(1-length(uv2));
uv += ring.xy *(1-omask)*.02;

vec3 retl = max(texture(sampler_main, uv-hor).xyz,texture(sampler_main, uv-hor.yx).xyz);
vec3 retr = texture(sampler_main, uv+hor).xyz;
vec3 rets = max (retl,retr)*min(0.3+rad*2,1);
struc = max(max(texture(sampler_main, uv).xyz,rets)*vec3(2,2,.7+1-2*blue)*1.3,((texture(sampler_blur2, uv).xyz * scale2) + bias2)*q1*3);
ret = struc; 

uv3 = (ring.x*0.2+ (uv2-0)*.25 +0.5+centre/aspect.xy);
uv3 = fract(uv3);
ret += ((texture(sampler_blur1, uv3).xyz * scale1) + bias1)*sat(2-length(uv2));

ret += texture(sampler_noise_hq,uv2/4+ring.x*0.1).r*blue* !mask*sat(1.3-length(uv2)) * vec3(0.2,0,1);


z = 1/(2-uv.y) ;
uv3 = vec2(uvc.x-.5,1)*z*vec2(q8,q9)+vec2(0,time*0.01);
uv3.y -= noise3(uv3).r*-0.008;
uv4 = ((uvc-vec2 (q12,0.3))*.4 +0.5+centre/aspect.xy);
ret += !mask*clamp(1-2*uvc.y, 0.0, 1.0)*2*clamp(noise3(uv3).r*((texture(sampler_blur2, uv4).xyz * scale2) + bias2)+vec3(0,0.1,0.25), 0.0, 1.0);
}

