beatb=0;
beatb2=0;
lbbtime2=time;
rawbeatb=0;
lbbtime=time;
avgdb=.01;
vol=0;
avgvol=1;
ph=0;
phase=0;
bool=0;
const=.01;
mtime=100+bass_att*1000;
