float a,b,c;
shader_body
{
vec2 uv2 = uv;
if(uv2.x >= 0.5) {uv2.x = 1-uv2.x;}
if(uv2.y >= 0.5) {uv2.y = 1-uv2.y;}

//float cs = cos(0.25*3.14159);
