//uv2.y = uv3.x * sn + uv3.y * cs;

uv.x=uv2.x;
uv.y=uv2.y;


    vec3 base = ((texture(sampler_blur3, uv).xyz * scale3) + bias3) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2) + ((texture(sampler_blur1, uv).xyz * scale1) + bias1) + texture(sampler_main, uv).xyz*0.886 - 0.466 ;
a =base.x/12;b = base.y/8; c = base.x/3;
c = c + b + a-b;
a = a + c + a*c;
base = 0.2+c;
ret = base*(0.552-base)*(1-base)*2*vec3(0.55,1.688,2.25) + pow(base,2)*vec3(2.29,1.688,1.74); //MilkDrop2077 color mod
//ret.xy = ret.yx;
ret = ret*(1-ret)*4; //MilkDrop2077 solarize effect
//MilkDrop3 Color Mode:
ret /= vec3(-1.9,-1.7,-2.6);
//MilkDrop3 Blow Mode:
vec3 avg_col = ((texture(sampler_blur2, uv).xyz * scale2) + bias2);ret = abs(avg_col - ret)*2;
//MilkDrop3 Shadows Mode:
ret += pow(texture(sampler_main,vec2(uv.x,1-uv.y)).yyy+texture(sampler_main,1-uv).yyy,vec3(1,2,3));
//MilkDrop3 Burn Mode:
ret = mix(ret,2,texture(sampler_main, uv).xyz.z*1.36);
}

