warp:
shader_body
{
  // sample previous frame
  ret = texture( sampler_pw_main, uv ).xyz*1.26;

  float n;
  vec2 d = texsize.zw*1;
  n  = clamp(texture(sampler_pw_main, uv+d*vec2( 1, 1)).x, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2( 3, 0)).x, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2( 1,-1)).x, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2( 0, 1)).x, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2( 0,-1)).x, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2(-1, 1)).x, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2(-1, 0)).x, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2(-1,-1)).x, 0.0, 1.0);
vec4 noise9 = texture(sampler_noisevol_hq, 
                      ((uv.xyy*q27
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(.5,2.3,2.9)*0.05 + 
                      time*vec3(.1,2.1,1.6)*q29
                     ).xyz;
if(ret.x> q21*q13         && ret.x<= q24*q11        )ret.y += (noise9).x*.5; 
if(ret.y> q22*q11         && ret.y<= q25*q12        )ret.z += (noise9).y*.5; 
if(ret.z> q23*q12         && ret.z<= q26*q13        )ret.x += (noise9).z*.5; 
float t = 1.4;
  if (n < 1.2)
    ret -= t;
  if (n > 3.1)
    ret -= t;
  if (ret.x < 0.09 && abs(n-3) < 0.58)
    ret += t;

  ret *= clamp(6 - 19*(((texture(sampler_blur1, uv_orig).xyz * scale1) + bias1).x), 0.0, 1.0);

  float dist_from_bar = abs(uv_orig.y - fract(time*0.23));
  //ret *= clamp( 24*dist_from_bar - 0.46 , 0.0, 1.0);  

  // darken (decay) over time
  ret = (ret - 0.016)*0.985;
}

comp:
shader_body
{

vec2 uvm = uv;
ret = texture(sampler_main, uvm).x,

ret = mix (
((texture(sampler_blur1, uvm).xyz * scale1) + bias1).x + ((texture(sampler_blur3, uvm).xyz * scale3) + bias3).x,
ret,
pow(ret,0.42));

ret *= 3.02;

vec2 uv3 = (uv-0.44)*(1-((texture(sampler_blur1, uvm).xyz * scale1) + bias1).zzz)+0.44;

ret += clamp(pow(texture(sampler_main, uv3).zzz,0.52)*(1-((texture(sampler_blur1, uvm).xyz * scale1) + bias1).x -ret.x), 0.0, 1.0),
ret += clamp(pow(((texture(sampler_blur1, uv3).xyz * scale1) + bias1).zzz,0.46)*(1-((texture(sampler_blur1, uvm).xyz * scale1) + bias1).x -ret.x), 0.0, 1.0),

ret = pow(ret,vec3(2.1,0.8,1))*1.413 -0.245; //MilkDrop2077 color mod
ret -= texture(sampler_main, uvm).yyy;
ret *= ret;

ret-= 0.046;
vec4 noise9 = texture(sampler_noisevol_hq, 
                      ((uv3.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(2.3,2.4,0.8)*0.05 +  //MilkDrop2077 color mod
                      time*vec3(2.9,2.2,0.5)*q30  //MilkDrop2077 color mod
                     ).xyz;
if(ret.x> q26*q3         && ret.x<= q23*q1        )ret.z -= (noise9).x*0.54; 
if(ret.y> q25*q1         && ret.y<= q22*q2        )ret.x -= (noise9).y*0.46; 
if(ret.z> q24*q2         && ret.z<= q21*q3        )ret.y -= (noise9).z*0.57; 

}

