frame_eqs_eel:
decay=1;

//vol= (bass+mid+treb)*0.55;
vol=vol;


mv_r = 0.5 + 0.4*sin(time*1.324);
mv_g = 0.5 + 0.4*cos(time*1.371);




zoom=1;

musictime=musictime+vol;
q4=sin(musictime*0.02)*0.3;
q5=sin(musictime*0.01)*0.3;

dx=sin(musictime*0.1)*0.01;
dy=tan(musictime*0.069)*0.01;




monitor=rot*ang;

db = db*0.98 + bass*0.2;
bb = bb + db*0.1;

dt = dt*0.98 + treb*0.2;
tt = tt + dt*0.1;

dm = dm*0.98 + mid*0.2;
mm = mm + dm*0.1;


w = (bb-tt)*0.4;
q11 = sin(w);
q12 = tan(w);
q13 = 0.5 + sin((bb-mm)*0.1)*0.25; // cx
q14 = 0.5 + sin((tt-mm)*0.1)*0.25; // cy
q15 = 4;
q16 = 0.06;
q17 = tan(-w);
q18 = cos(-w);

pixel_eqs_eel:
da=.13133;//+sin(time/17) /10;
nh=2;
nh2=1;
myx=(-int(x*nh)+x*nh-.4);
myy=(-int(y*nh2)+y*nh2-.5);//+sin(time/11) /22);

myx=sin(x*6.28+time/1.6);
myy=sin(y*6.28+time/9);

//myy = y*.5-.35;///+sin(time/8)/40;
