dx = da*(2*myx*myy);
dy = da*((myy*myy) - (myx*myx));

warp:
uniform sampler2D sampler_Pattern_046;

shader_body
{
    // sample previous frame
   ret = texture( sampler_Pattern_046, uv).xyz;
}

comp:
sampler MilkDrop3_001;
uniform sampler2D sampler_MilkDrop3_008;

shader_body
{


    vec2 d = texsize.zw*12;
    vec3 dx = ( ((texture(sampler_blur1, uv + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    vec3 dy = ( ((texture(sampler_blur1, uv + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );
    vec2 my_uv = uv + vec2(dx.y,dy.y)*0.1;

vec3 re;

//uv = uv + 0.02*vec2(cos(uv.y*27),sin(uv.x*39));
vec2 m = vec2(1-q4,q8);
vec2 bg_uv = m + (uv-m)*0.992; // subtle 3D shadow drop
vec2 mid_uv = m + (uv-m)*0.996;
ret = mix( vec3(0,0,0.2), vec3(0.3,0,1),texture( sampler_fw_main, bg_uv).y);
ret = mix(ret, vec3(1,1,0), max(((texture(sampler_blur1, bg_uv).xyz * scale1) + bias1).z*1.5,texture( sampler_main, mid_uv).z)); // if that isn't a cool glow effect?
ret *= 1-((texture(sampler_blur1, bg_uv).xyz * scale1) + bias1).x*2; // drop shadow
ret = mix( ret, vec3(1,0.01,0.4),texture( sampler_fw_main, uv).x);
//ret = texture(sampler_main, uv).xyz;
re = texture( sampler_MilkDrop3_008, my_uv).xyz;
re = mix( texture(MilkDrop3_001,my_uv).xyz, re, ret.x);
ret = mix( re, 1-re, ret.y);

//MilkDrop3 Color Mode:
ret /= vec3(0.5,1.3,1.4);
}

