    float3 dy = ( GetBlur1(uv + float2(0,1)*d) - GetBlur1(uv-float2(0,1)*d) );
    float2 my_uv = uv + float2(dx.y,dy.y)*0.1;

float3 re;

//uv = uv + 0.02*float2(cos(uv.y*27),sin(uv.x*39));
float2 m = float2(1-q4,q8);
float2 bg_uv = m + (uv-m)*0.992; // subtle 3D shadow drop
float2 mid_uv = m + (uv-m)*0.996;
ret = lerp( float3(0,0,0.2), float3(0.3,0,1),tex2D( sampler_fw_main, bg_uv).y);
ret = lerp(ret, float3(1,1,0), max(GetBlur1(bg_uv).z*1.5,tex2D( sampler_main, mid_uv).z)); // if that isn't a cool glow effect?
ret *= 1-GetBlur1(bg_uv).x*2; // drop shadow
ret = lerp( ret, float3(1,0.01,0.4),tex2D( sampler_fw_main, uv).x);
//ret = tex2D(sampler_main, uv);
re = tex2D( sampler_MilkDrop3_008, my_uv);
re = lerp( tex2D(MilkDrop3_001,my_uv), re, ret.x);
