comp:
vec2 d, d_uv;
vec3 dx, dy;

shader_body
{
//MilkDrop3 Zoom Mode:
uv = 0.05+ 0.9*uv;


ret = 3*((texture(sampler_blur3, uv).xyz * scale3) + bias3);

vec2 uv2 = uv;

uv *= (0.875-.06*cos(2*(ret.xx+ret.yy)));
uv += .03*cos(2*(ret.xx+ret.yy))+.075;

   d = texsize.zw*4;
   d_uv = uv;
   dx = ((texture(sampler_blur1, d_uv + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, d_uv - vec2(1,0)*d).xyz * scale1) + bias1);
   dy = ((texture(sampler_blur1, d_uv + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, d_uv - vec2(0,1)*d).xyz * scale1) + bias1);
   d_uv += vec2(dx.x,dy.x)*0.03;

ret = mix(((texture(sampler_blur2, d_uv).xyz * scale2) + bias2),texture(sampler_main, d_uv).xyz,0.4);
ret = (ret.y-ret.x*0.5)*mix(vec3(2.5,0.6,0),1,ret.x)*1.4;

ret = mix(ret,0,((texture(sampler_blur1, d_uv-vec2(1,1)*texsize.zw*32).xyz * scale1) + bias1).z*0.6);
ret = mix(ret,2,texture(sampler_main, uv2).xyz.z*0.26);
ret *= ret+0.5;
//ret = texture(sampler_main, uv).xyz;
//MilkDrop3 Color Mode:
ret /= vec3(1.5,1.1,0.8);
}

