t2 = .5;

sp = sample*6.283;
spp = sample + (cos(sp*6)*.2);
tm = time*.5 + sp;


ax = sin(tm)*spp;
ay = cos(tm)*spp;
az = sin(tm*3)*spp;

bx = ax;
by = ay*cos(t2) - az*sin(t2);
bz = ay*sin(t2) + az*cos(t2);

ax = bx*cos(t1) - bz*sin(t1);
ay = by;
az = bx*sin(t1) + bz*cos(t1);


invz = .5*(1/(az+2));
