pixel_eqs_eel:
du = (x*2-1) - q1;
dv = (y*2-1) - q2;
dist = sqrt(du*du+dv*dv);
ang2 = atan2(du,dv) + time*0.159;
mult = 0.04*sin(dist*0.05);
dx = mult*sin(ang2*2-1.5)*aspectx;
dy = mult*cos(ang2*2-1.5)*aspecty;

warp:
shader_body
{
    // PAINTERLY EFFECT:
    vec2 t = 0.005 + 0.045*clamp(vec2(bass,treb)-1, 0.0, 1.0);
    vec2 uv2 = uv;
    vec2 delta2 = texsize.zw*vec2(1,1);
    vec3 blurry_color = texture( sampler_main, 
        mix(uv,uv_orig,-1) + delta2 ).xyz;
    vec2 v = (blurry_color.xy-0.37) * t;
      //uv2 += v;
      // instead, add a rotated version:
      uv2.x += v.x*q1 + v.y*q2;
      uv2.y += v.x*q2 - v.y*q1;

    // sample previous frame
    ret =texture( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;    
}

comp:
shader_body
{

    vec2 d = texsize.zw*4;
    float dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) ).y;
    float dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) ).y;
    vec2 uv_y = uv-vec2(dx,dy)*.7;

ret =mix(vec3(2.6,2,2)*texture(sampler_fc_main, uv_y).x,
                              vec3(2.6,2,3), ((texture(sampler_blur1, uv).xyz * scale1) + bias1).y*0.05);

ret =mix(ret,vec3(-.2,-0.2,-0.3),texture(sampler_main, uv).xyz.z);

//MilkDrop3 Invert Mode:
ret =1 - ret;
//MilkDrop3 Color Mode:
ret /= vec3(2.9,2.6,2.5);
//MilkDrop3 Blow Mode:
vec3 avg_col = ((texture(sampler_blur2, uv).xyz * scale2) + bias2);ret =abs(avg_col - ret)*2;
}

warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 t = 0.005 + 0.045*saturate(float2(bass,treb)-1);
    float2 uv2 = uv;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, 
        lerp(uv,uv_orig,-1) + delta2 ).xyz;
    float2 v = (blurry_color.xy-0.37) * t;
      //uv2 += v;
      // instead, add a rotated version:
      uv2.x += v.x*q1 + v.y*q2;
      uv2.y += v.x*q2 - v.y*q1;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;    
}

comp_hlsl:
shader_body
{

    float2 d = texsize.zw*4;
    float1 dx = ( GetBlur1(uv+float2(1,0)*d)-GetBlur1(uv-float2(1,0)*d) ).y;
    float1 dy = ( GetBlur1(uv+float2(0,1)*d)-GetBlur1(uv-float2(0,1)*d) ).y;
    float2 uv_y = uv-float2(dx,dy)*.7;

ret = lerp(float3(2.6,2,2)*tex2D(sampler_fc_main, uv_y).x,
                              float3(2.6,2,3), GetBlur1(uv).y*0.05);

ret = lerp(ret,float3(-.2,-0.2,-0.3),GetPixel(uv).z);

//MilkDrop3 Invert Mode:
ret = 1 - ret;
//MilkDrop3 Color Mode:
ret /= float3(2.9,2.6,2.5);
//MilkDrop3 Blow Mode:
float3 avg_col = GetBlur2(uv);ret = abs(avg_col - ret)*2;
}
