frame_eqs_eel:
// raytraced sphere code

d = 20; // distance of the sphere, the farther you're away the more you'll see on the horizon
z = 4; // distance of the pane where the scene is projected on, wider distance = smaller apex angle of the camera, very small distances = fish eyes view

zz = 1 + bass_att*0; // linear zoom

pi = asin(1)*2;
pi2inv = 0.5/pi;
maskangle = asin( 1/d);
q32 = maskangle; // hit = (cameras angle < mask_angle)
q31 = d;
q30 = z;
q29 = pi2inv;
q28 = pi;
q27 = 1/zz;

v = 0.4;
j1 = j1*0.95 + sqr(bass*4)*v;
j2 = j2*0.95 + sqr(mid*4)*v;
j3 = j3*0.95 + sqr(treb*4)*v;

n = n + j1*0.0152;
n1 = n1 + j2*0.0152;
n2 = n2 + j3*0.0152;

vol=(bass+mid+treb)*0.25;
vol=vol;
q3=vol;
mtime = mtime+vol*0.01;
q1=mtime;

zoom = zoom + 0.015*vol;

w1 = n*0.01;    // solid angles to rotate the sphere in its origin
w2 = n1*0.01;
w3 = n2*0.01;

q20 = cos(w1);
q21 = sin(w1);
q22 = cos(w2);
q23 = sin(w2);
q24 = cos(w3);
q25 = sin(w3);

warp:
shader_body
{
    // PAINTERLY EFFECT:
    vec2 t = 0.0005 + 0.045*clamp(vec2(bass,treb)-1, 0.0, 1.0);
    vec2 uv2 = uv;
    vec2 delta2 = texsize.zw*vec2(1,1);
    vec3 blurry_color = texture( sampler_main, 
        mix(uv,uv_orig,-1) + delta2 ).xyz;
    vec2 v = (blurry_color.xy-0.37) * t;
      //uv2 += v;
      // instead, add a rotated version:
      uv2.x += v.x*q1 + v.y*q2;
      uv2.y += v.x*q2 - v.y*q1;

    // sample previous frame
    ret = texture( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.006;    
}

comp:
shader_body
{
//MilkDrop3 Flip Mode:
uv = 1-uv;





//MilkDrop3 Rotate Mode:
uv = uv.xy;


    vec2 d = texsize.zw*4;
    float dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) ).y;
    float dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) ).y;
    vec2 uv_y = uv-vec2(dx,dy)*.7;

ret = mix(vec3(2.6,2,2)*texture(sampler_fc_main, uv_y).x,
                              vec3(2.6,2,3), ((texture(sampler_blur1, uv).xyz * scale1) + bias1).y*0.05);

ret = mix(ret,vec3(-.2,-0.2,-0.3),texture(sampler_main, uv).xyz.z);

//MilkDrop3 Invert Mode:
ret = 1 - ret;
//MilkDrop3 Color Mode:
ret /= vec3(2.9,2.6,2.5);
//MilkDrop3 Blow Mode:
vec3 avg_col = ((texture(sampler_blur2, uv).xyz * scale2) + bias2);ret = abs(avg_col - ret)*2;
//MilkDrop3 SoftColor Mode:
ret += vec3(-0.2,0.1,0.3);
}

warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 t = 0.0005 + 0.045*saturate(float2(bass,treb)-1);
    float2 uv2 = uv;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, 
        lerp(uv,uv_orig,-1) + delta2 ).xyz;
    float2 v = (blurry_color.xy-0.37) * t;
      //uv2 += v;
      // instead, add a rotated version:
      uv2.x += v.x*q1 + v.y*q2;
      uv2.y += v.x*q2 - v.y*q1;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.006;    
}

comp_hlsl:
