waves.point_eqs_eel:
x = .5 + (bass*.2)*sin(sample*2*(time*10*treb));
y = .5 + (bass*.2)*cos(sample*2*(time*10*treb));
r = 1 + .5*sin(sample*.10+(time*10*bass));
g = 1 + .5*sin(sample*2+(time*50*treb));
b = 1 + .5*sin(sample*5+(time*20*mid));
a = r;

frame_eqs_eel:
wave_r = wave_r + 0.400*( 0.60*sin(0.933*time) + 0.40*sin(1.045*time) );
wave_g = wave_g + 0.100*( 0.60*sin(0.900*time) + 0.40*sin(0.956*time) );
wave_b = wave_b + 0.100*( 0.60*sin(0.910*time) + 0.40*sin(0.920*time) );
mv_r = wave_r;
mv_b = wave_b;
mv_g = wave_g;
q1 = 0.05*(pow(1+1.2*bass+0.4*bass_att+0.1*treb+0.1*treb_att+0.1*mid+0.1*mid_att,6)/fps);
//q1 = 0.6*(bass+bass_att);
mv_a = if(above(bass-1.2,1),1,bass-1.2);
q2 = oldq2 + 0.05*(pow(1+1.2*treb+0.4*treb_att+0.1*treb+0.1*treb_att+0.1*mid+0.1*mid_att,6)/fps);

//q2 = oldq2 +q1;
oldq2 = q2 ;
monitor = q2;

warp:
shader_body
{
   float d = texsize.zw;
   vec3 deltax = (texture(sampler_main, uv + vec2(1,0)*d).xyz + texture(sampler_main, uv + vec2(1,0)*d).xyz)*0.5;    
   vec3 deltay = (texture(sampler_main, uv + vec2(0,1)*d).xyz + texture(sampler_main, uv + vec2(0,1)*d).xyz)*0.5;    


   ret = ((deltax+deltay - texture(sampler_main, (uv-0.5)*0.9 + 0.5 ).xyz)); 
   ret -= 0.4;
}

comp:
shader_body
{

    uv = 0.05 + 0.9*uv;
    ret = ((texture(sampler_blur1, uv).xyz * scale1) + bias1);
    uv += -.05*asin(3*ret.yx)+.05*cos(2*ret.zz)-0.025;

    ret = texture(sampler_main,uv).xyz;
    ret += ((texture(sampler_blur1, uv).xyz * scale1) + bias1);

    // palettize:
 //   ret = pow(lum(ret), vec3(0.3, .76, 0.87));

    // invert
    ret = clamp(ret, 0.0, 1.0);
    ret *= ret;
    //ret.xyz *= 2; // a little bit of overbright
//MilkDrop3 Color Mode:
ret /= vec3(1.0,0.8,0.7);
}

warp_hlsl:
shader_body
{
   float d = texsize.zw;
   float3 deltax = (GetPixel(uv + float2(1,0)*d) + GetPixel(uv + float2(1,0)*d))*0.5;    
   float3 deltay = (GetPixel(uv + float2(0,1)*d) + GetPixel(uv + float2(0,1)*d))*0.5;    


   ret = ((deltax+deltay - tex2D(sampler_main, (uv-0.5)*0.9 + 0.5 ))); 
   ret -= 0.4;
}

comp_hlsl:
shader_body
{

    uv = 0.05 + 0.9*uv;
    ret = GetBlur1(uv);
    uv += -.05*asin(3*ret.yx)+.05*cos(2*ret.zz)-0.025;

    ret = tex2D(sampler_main,uv);
    ret += GetBlur1(uv);

    // palettize:
 //   ret = pow(lum(ret), float3(0.3, .76, 0.87));

    // invert
    ret = saturate(ret);
    ret *= ret;
    //ret.xyz *= 2; // a little bit of overbright
//MilkDrop3 Color Mode:
ret /= float3(1.0,0.8,0.7);
}
