waves.frame_eqs_eel:
vol = bass*8 + mid*5 + treb*3;
m = m*0.97 + vol*0.08;
monitor = vol;
beat = above(vol,res)*above(vol,m)*above(vol,16);
diff = (1-beat)*diff + beat*(vol-res);
res = beat*(vol + m*0.04) + (1-beat)*(res -  (0.1+diff*0.02)*60/fps);
res = max(0,res);

a = beat;

frame_eqs_eel:
red_value = if(above(bass,1.15),3*(1-bass)+abs(sin(time*0.3)*0.1),if(above(treb,1.0),0, sin(time/2.5)/10+0.65));
blue_value = if(above(bass,1.15),0.5+abs(sin(time*0.3)*0.1),if(above(treb,1.0),0.7+0.1*sin(time*1.123), 0.3-treb-sin(time/2.5)/10));
green_value = if(above(bass,1.15),1-abs(sin(time*0.3)*0.1),if(above(treb,1.0),0.6+0.1*sin(time*1.123), sin(time/2.5)/10+0.3));
wave_r = red_value;
wave_b= blue_value;
wave_g = green_value;
q1 = (cx*2-1) + 0.62*( 0.60*sin(0.374*time) + 0.40*sin(0.294*time) );
q2 = (cy*2-1) + 0.62*( 0.60*sin(0.393*time) + 0.40*sin(0.223*time) );
q3 = (cx*2-1) + 0.62*( 0.60*sin(0.174*-time) + 0.40*sin(0.364*time) );
q4 = (cy*2-1) + 0.62*( 0.60*sin(0.234*time) + 0.40*sin(0.271*-time) );
decay = decay - 0.01*equal(frame%5,0);

pixel_eqs_eel:
du = (x+rad)*2-1 - q1;
dv = (y+rad)*2-1 - q2;
dist = sqrt(du*du+dv*dv);
ang2 = atan2(du,dv);
mult = 0.008/(dist+0.4);
dx = mult*sin(ang2-1.5)/4;
dy = mult*cos(ang2-1.5)/4;
du = (1-rad)*2-1 - q3;
dv = (1-rad)*2-1 - q4;
dist = sqrt(du*du+dv*dv);
ang2 = atan2(du,dv);
mult = 0.008/(dist+0.4);
dx = dx + mult*sin(ang2+1.5)/4;
dy = dy + mult*cos(ang2+1.5)/4;

warp:
shader_body
{
    vec3 N = texture(sampler_noise_lq, rand_frame.xy + uv*
                             texsize_noise_lq.zw*texsize.xy ).xyz*2-1;
    // sample previous frame
    ret =texture( sampler_pw_main, uv + N.zw*texsize.zw*0.5 ).xyz;
    ret += (0.59-0.2*rad)*N.xyz;


    ret =clamp((ret-0.5)*3 + 0.25, 0.0, 1.0);
    ret.yz = ret.x;
}

comp:
shader_body
{

ret =mix(((texture(sampler_blur2, uv).xyz * scale2) + bias2),texture(sampler_main, uv).xyz,0.4);

ret =(ret.y-ret.x*0.5)*mix(vec3(2.5,.9,2),1,ret.x)*1.4;

//MilkDrop3 Brightness Mode:
ret *= 1.6;
}

warp_hlsl:
shader_body
{
    float4 N = tex2D(sampler_noise_lq, rand_frame.xy + uv*
                             texsize_noise_lq.zw*texsize.xy )*2-1;
    // sample previous frame
    ret = tex2D( sampler_pw_main, uv + N.zw*texsize.zw*0.5 ).xyz;
    ret += (0.59-0.2*rad)*N.xyz;


    ret = saturate((ret-0.5)*3 + 0.25);
    ret.yz = ret.x;
}

comp_hlsl:
shader_body
{

ret = lerp(GetBlur2(uv),GetPixel(uv),0.4);

ret = (ret.y-ret.x*0.5)*lerp(float3(2.5,.9,2),1,ret.x)*1.4;

//MilkDrop3 Brightness Mode:
ret *= 1.6;
}
