frame_eqs_eel:
dec_med = pow (0.9, 30/fps);
dec_slow = pow (0.96, 30/fps);
dec_fast = pow (0.6, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, 0+avg+peak) * above (time, t0+.1);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %16;
index2 = (index2 + is_beat*bnot(index))%8;

q20 = avg;
q21 = beat;
q22 = peak;
q24 = is_beat;
vol = bass_att + mid_att + treb_att;
vol_ = dec_med * vol_ + (1-dec_med)* vol;
vol__ = dec_med * vol__ + (1-dec_med)* vol_;
q27 = index + 1;
q28 = index2 + 1;
q23 = q22 -0.1/q22;
q23 = max(q23,1);

k1 =  is_beat*equal(index%8,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_fast * p2+ (1-dec_fast)*p1;
rott = p2 * 3.1416/4;

q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;

trig = q24 * bnot(index%2);
vx = vx*bnot(trig) + trig* (int(rand(100))/100-.5);
vy = vy*bnot(trig) + trig* (int(rand(100))/100-.5);
q10 = 0.2+vy*vy*2;
q11 = sin(time/9)/2+.4;
movz = movz-1/fps*(.3+vx);
