//dir_ = dec_med*dir_ + (1-dec_med)*(index2%2-.5);
dir_ =  bass-1;

trig = bnot(index%4) * q24;
dir = bnot(trig)*dir + trig*(int(rand(10))-5);

trel = trel + .1/fps*dir;
trel_ = dec_med*trel_ + (1-dec_med)*trel;

q5 = cos(trel_);
q6 = sin(trel_+0*q27*(q28%2));
q7 = -q6;
q8 = q5;

