comp:
shader_body
{
    ret = texture(sampler_main, uv).xyz*0.7 + ((texture(sampler_blur2, uv).xyz * scale2) + bias2)*2.75;
    ret *= 0.89;
ret /= vec3(1.2,1.5,.8);
//MilkDrop3 Color Mode:
ret /= vec3(0.9,-3.0,0.5);
//MilkDrop3 Burn Mode:
ret = mix(ret,2,texture(sampler_main, uv).xyz.z*1.36);
}

comp_hlsl:
