{
    ret = GetPixel(uv)*0.7 + GetBlur2(uv)*2.75;
    ret *= 0.89;
ret /= float3(1.2,1.5,.8);
//MilkDrop3 Color Mode:
ret /= float3(0.9,-3.0,0.5);
//MilkDrop3 Burn Mode:
