warp:
shader_body
{
  vec2 uv2 = mix(uv_orig, uv, 0.2);

  // diffused version:
  float ss = 0.3;
  vec3 blur = 0.25*(
    texture(sampler_main, uv2+ss*texsize.zw*vec2( 1, 1)).xyz +
    texture(sampler_main, uv2+ss*texsize.zw*vec2(-1, 1)).xyz +
    texture(sampler_main, uv2+ss*texsize.zw*vec2( 1,-1)).xyz +
    texture(sampler_main, uv2+ss*texsize.zw*vec2(-1,-1)).xyz
  );

  // diffusion:  
  ret = texture(sampler_main,uv2).xyz;//blur;

  float F2 = 0.035;
  float k2 = 0.060;
  float s = 0.025;
  float scale = 9;

  // reaction:
  vec3 z = ret;
  float uvv = ret.x*ret.y*ret.y;
  z.x += (-uvv + F2*(1-ret.x) + 0.0007)*scale;
  z.y += (uvv - (F2+k2)*ret.y)*scale;
  //z.x += s*(16-ret.x*ret.y);
  //z.y += s*(ret.x*ret.y - ret.y - ret.z);
  z.z -= 0.02;

  // apply laplacian:
  z.x += (blur - texture(sampler_main, uv2).xyz)*0.2*scale;

  ret = z;  
  //ret *= 0.98 + 0.02*clamp( (0.75 - rad )*10 , 0.0, 1.0);
  //ret *= 0.99;

  // add noise:
  ret.x += 0.05*(tex2d(sampler_noise_lq, uv*texsize.xy*texsize_noise_lq.zw + rand_frame.xy).xyz*2-1);

  // darken (decay) over time
  //ret = (ret - 0.09)*0.95;
}

comp:
uniform sampler2D sampler_manyfish;
shader_body
{
    vec2 d = texsize.zw*4;
    vec3 dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    vec3 dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );
    vec2 uv_y = uv-vec2(dx.y,dy.y)*0.4;

    vec2 uv_dx = uv - vec2(dx.x,dy.x)*1;
    vec2 uv_dz = uv - vec2(dx.z,dy.z)*1;

ret = 0;
ret = mix(ret, vec3(1,1,0), length(vec2(dx.z,dy.z)));
vec4 noise9 = texture(sampler_noisevol_hq, 
                      ((uv_dx.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(1,1,0)*0.05 + 
                      time*vec3(2,0,1)*q31
                     ).xyz;
if(ret.x> q26*q13         && ret.x<= q23*q11        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q11         && ret.y<= q22*q12        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q12         && ret.z<= q21*q13        )ret.y -= (noise9).z*.5; 
ret = mix(ret,vec3(1,0,0),texture(sampler_main, uv).xyz.z*clamp(1-((texture(sampler_blur3, uv).xyz * scale3) + bias3).y*8, 0.0, 1.0));
ret = mix(ret,1,((texture(sampler_blur1, uv_dz).xyz * scale1) + bias1).x*((texture(sampler_blur1, uv).xyz * scale1) + bias1).z*2); 

ret = mix(ret, vec3(2,0,2), length(vec2(dx.x,dy.x)));
ret = mix(ret,vec3(3,1,0),texture(sampler_main, uv).xyz.x*clamp(1-((texture(sampler_blur3, uv).xyz * scale3) + bias3).y*8, 0.0, 1.0));
ret = mix(ret,vec3(2,1,1),((texture(sampler_blur1, uv_dx).xyz * scale1) + bias1).z*((texture(sampler_blur1, uv).xyz * scale1) + bias1).x*2); 

ret = mix(clamp(ret, 0.0, 1.0),0,((texture(sampler_blur1, uv).xyz * scale1) + bias1).y*4);
//ret += ((texture(sampler_blur1, uv).xyz * scale1) + bias1).y*((texture(sampler_blur1, uv_dz).xyz * scale1) + bias1).x*vec3(1,1,0)*4;
ret = mix(ret,vec3(0.4,0.7,0.8),texture(sampler_main, uv).xyz.y);

//ret = mix(ret,vec3(1,0,0),((texture(sampler_blur1, uv_dx).xyz * scale1) + bias1).z*clamp(1-((texture(sampler_blur3, uv).xyz * scale3) + bias3).y*8, 0.0, 1.0)*((texture(sampler_blur1, uv).xyz * scale1) + bias1).x*2);


}

