    ret.x += (ret.x - GetBlur3(uv).x)*.1;
    ret.x += 0.004;


//--------------------------------

    my_uv = uv + float2(dy,-dx)*0.05*(1.2-GetBlur3(uv).y);



    v = 0.01;
