init_eqs_eel:
trel = rand(10);
index = rand(8);
index2 = rand(8);
avg = 0;

frame_eqs_eel:
dec_med = pow (0.8, 30/fps);
dec_slow = pow (0.862, 30/fps);
beat = (bass + mid + treb)/3;
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, 0.22+avg+peak) * above (time, t0+0.22);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %8;
index2 = (index2 + is_beat*bnot(index))%8;

q20 = avg;
q21 = beat;
q22 = peak;
q23 = (bass+mid+treb)-(bass_att+mid_att+treb_att);
q23 = beat - avg;
q24 = is_beat;
q27 = index;
q28 = index2;
q19 = index;

k1 =  is_beat*equal(index,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott = p2 * 3.330088212805/2;
q1 = cos(rott);
q2 = sin(rott);

pulse = pulse*dec_slow+ (1-dec_slow)* peak;
winkel = winkel + pulse/fps*30;
q29 = winkel;

trel = trel + (bass_+mid_+treb_)/fps/3;
trel_ = trel_*dec_med + (1-dec_med)*trel;
q30 = trel_;
q31 = sin(trel_/4)*12;

dec_slow = pow (0.79, 30/fps);
bass_ = bass_*dec_slow + (1-dec_slow)*bass_att;
mid_  = mid_*dec_slow + (1-dec_slow)*(mid_att);
treb_ = treb_*dec_slow + (1-dec_slow)*treb_att;

q12 = sigmoid((bass_-treb_)*4,1); 
q13 = sigmoid((mid_-bass_)*4,1);
q14 = sigmoid((treb_-mid_)*4,1);

q15 = above(q20,1.59); //stopngo

rota = rota*dec_med + (1-dec_med)*q22/2;
zoom = 0.76 + 0.046*q1*50/fps;
cx = 0.44+0.25*sin(trel_*12) * bnot(q28%3);
cy = 0.38+0.49*cos(trel_*6) * bnot(q28%3);
rot = (0.032*q1+rota/8*q2)*50/fps;
dx = 0.0144*q1*50/fps*avg;
dy = 0.014*q2*50/fps*avg;

warp:
shader_body
{
uv = mix( uv_orig,uv,0.2);

vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw*1.5 + rand_frame.xy;
uv  -= (vec2(0,1)*(lum(texture(sampler_main, uv).xyz-0.35))*texsize.zw*32)*(lum(texture(sampler_main, uv).xyz)-0.4);
ret  = texture(sampler_main, clamp(uv, 0.0, 1.0)).xyz - 0.0011 + (texture(sampler_noise_lq, dither_uv).xyz-0.5)*0.0038;    
}

comp:
shader_body
{
    vec2 d = texsize.zw*4;
    float dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) ).y;
    float dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) ).y;
    vec2 uv_y = uv-vec2(dx,dy)*.5;

ret = mix(vec3(1.4,0.8,2)*texture(sampler_fc_main, uv_y).x,
                              vec3(1.6,2,3), ((texture(sampler_blur1, uv).xyz * scale1) + bias1).y*0.05);

ret = mix(ret,vec3(1,.1,-2),texture(sampler_main, uv).xyz.z);

}

warp_hlsl:
shader_body
{
uv = lerp( uv_orig,uv,0.2);

float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw*1.5 + rand_frame.xy;
uv  -= (float2(0,1)*(lum(GetPixel(uv)-0.35))*texsize.zw*32)*(lum(GetPixel(uv))-0.4);
ret  = GetPixel(saturate(uv)) - 0.0011 + (tex2D(sampler_noise_lq, dither_uv)-0.5)*0.0038;    
}

comp_hlsl:
shader_body
{
    float2 d = texsize.zw*4;
    float1 dx = ( GetBlur1(uv+float2(1,0)*d)-GetBlur1(uv-float2(1,0)*d) ).y;
    float1 dy = ( GetBlur1(uv+float2(0,1)*d)-GetBlur1(uv-float2(0,1)*d) ).y;
    float2 uv_y = uv-float2(dx,dy)*.5;

ret = lerp(float3(1.4,0.8,2)*tex2D(sampler_fc_main, uv_y).x,
                              float3(1.6,2,3), GetBlur1(uv).y*0.05);

ret = lerp(ret,float3(1,.1,-2),GetPixel(uv).z);

}
