float _pow(float b,float e){return pow(b,e);}
vec2  _pow(vec2  b,float e){return pow(b,vec2(e));}
vec3  _pow(vec3  b,float e){return pow(b,vec3(e));}
vec4  _pow(vec4  b,float e){return pow(b,vec4(e));}
shader_body
{
    vec3 base = ((texture(sampler_blur3, uv).xyz * scale3) + bias3) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2) + ((texture(sampler_blur1, uv).xyz * scale1) + bias1) + texture(sampler_main, uv).xyz*0.8 - 0.4 ;
a =base.x/4;b = base.y/4; c = base.z/4;
c = c + b + a*b;
a = a + c + a*c;
base = .4*a;
ret =base*(1.3-base)*(.7-base)*2*vec3(1.2,0.4,2.2) + _pow(base,2)*vec3(2.9,2.5,0.8); //MilkDrop2077 color mod
ret.xy = ret.yx;
ret /= vec3(2.8,-1.9,-0.9);
}

warp_hlsl:
