warp:
shader_body
{
    vec2 scale = vec2(1280,1024)*texsize.zw; // 1280x1024 : the resolution i modelled this shader with
    float d = 0.005;
    float dx = ( ((texture(sampler_blur2, uv + vec2(d,0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(d,0)).xyz * scale2) + bias2) ).x*scale.x;
    float dy = ( ((texture(sampler_blur2, uv + vec2(0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(0,d)).xyz * scale2) + bias2) ).x*scale.y;
    float dxb = ( ((texture(sampler_blur2, uv + vec2(d,0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(d,0)).xyz * scale2) + bias2) ).x*scale.x;
    float dyb = ( ((texture(sampler_blur2, uv + vec2(0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(0,d)).xyz * scale2) + bias2) ).x*scale.y;
    vec2 my_uv = uv - vec2(dx,dy)*0.008 + vec2(dxb,dyb)*0.0034;



    vec2 v = 0.01;
    ret.x = texture( sampler_fw_main, my_uv -floor(my_uv)*0).x;


    ret.x += (ret.x - ((texture(sampler_blur3, uv).xyz * scale3) + bias3).x)*.1;
    ret.x += 0.004;


//--------------------------------

    my_uv = uv + vec2(dy,-dx)*0.05*(1.2-((texture(sampler_blur3, uv).xyz * scale3) + bias3).y);



    v = 0.01;
    ret.z = texture( sampler_fw_main, my_uv).z;

    ret.z += (ret.z - ((texture(sampler_blur1, uv).xyz * scale1) + bias1).z)*length(my_uv-uv)*180/length(scale);
    ret.z *= 0.8;
    ret.z += 0.004;



//--------------------------------

    d = 0.009;
    my_uv = vec2(-dy,dx)*0.045;

    dx = ( ((texture(sampler_blur2, uv + vec2(d,0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(d,0)).xyz * scale2) + bias2) ).y*scale.x;
    dy = ( ((texture(sampler_blur2, uv + vec2(0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(0,d)).xyz * scale2) + bias2) ).y*scale.y;
    my_uv += uv - vec2(dx,dy)*0.025;



    v = 0.01;
    ret.y =texture( sampler_fw_main, my_uv).y;


    ret.y += (ret.y - ((texture(sampler_blur3, my_uv).xyz * scale3) + bias3).y)*0.1 + 0.01;
//ret = 0;
}

comp:
shader_body
{
    vec2 d = texsize.zw;
    vec3 dx = ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz );
    vec3 dy = ( texture(sampler_main, uv+vec2(0,1)*d).xyz-texture(sampler_main, uv-vec2(0,1)*d).xyz );
vec2 dz = vec2(dx.y,dy.y)*3;

d = texsize.zw*2;
dx = ( 2*((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-2*((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) )*0.5;
dy = ( 2*((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-2*((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) )*0.5;

dz +=vec2(dx.y,dy.y);

vec3 bg = pow(length(dz)*0.8+0,0.7) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2).y*0.4-0.1;

ret = bg*vec3(2.3,1.5,.7);
ret = mix(ret,vec3(0.4,0.2,0),texture(sampler_main, uv + vec2(dx.x,dy.x)*texsize.zw*18).xyz.x*6);
ret = mix(ret,1,texture(sampler_main, uv).xyz.z);

//ret = texture(sampler_main, uv).xyz;
//MilkDrop3 Rainbow Mode:
ret *= 1*normalize ((texture (sampler_noise_hq,(uv+q4/2)/6).xyz));
}

warp_hlsl:
