pixel_eqs_eel:
mkdx=dx;//////////////////////////////////////////
mkdy=dy;
mksx=sx;
mksy=sy;
mkrt=rot;
mkzm=zoom;
mkcx=cx;
mkcy=cy;
mkwp=warp;

dx=0;
dy=0;
sx=1;
sy=1;
rot=0;
zoom=1;
cx=0.52;
cy=.5;
warp=0;/////////////////////////////////////////////////////

dx=dx-0.05*cos(time*19.63+.5+(rad*2-1)*(sin(time*1.26) *6 ));
dy=dy-0.012653*sin((ang*2-1)*int(tan(time/27) *3+6));
zoom=800+sin(time/bass);
///////////////////////////////////////
cr1=(int(sin(time*30)/2.5+.5+.1));
cr2=cr1;
crx=cr2-cr1;
ci=rad;
cb=0.237  ;
crs=2;
cr=pow(sin(ci*6.3-(cb*6.3))/2+.5,crs)*crx+cr1;
cqr=if( above (ci,.25+cb),cr,crx+cr1);crr=if( below(ci,1.67),cqr,crx+cr1);
jx1=crr;
kx1=1-crr;

mjdx=dx;
mjdy=dy;
mjsx=sx;
mjsy=sy;
mjrt=rot;
mjzm=zoom;
mjcx=cx;
mjcy=cy;
mjwp=warp;

dx=(jx1*mjdx+kx1*bass/300);
dy=(jx1*mjdy+kx1*mkdy);
sx=(jx1*mjsx+kx1*mksx);
sy=(jx1*mjsy+kx1*mksy);
rot=(jx1*mjrt+kx1*mkrt);
zoom=(jx1*mjzm+kx1*mkzm);
cx=(jx1*mjcx+kx1*mkcx);
cy=(jx1*mjcy+kx1*mkcy);
warp=(jx1*mjwp+kx1*mkwp);////////////////////////////////////////////////////

warp:
shader_body
{
    // PAINTERLY EFFECT:
    vec2 t = 0.005 + 0.045*clamp(vec2(bass,treb)-1, 0.0, 1.0);
    vec2 uv2 = uv;
    vec2 delta2 = texsize.zw*vec2(1,1);
    vec3 blurry_color = texture( sampler_main, 
        mix(uv,uv_orig,-1) + delta2 ).xyz;
    vec2 v = (blurry_color.xy-0.37) * t;
      //uv2 += v;
      // instead, add a rotated version:
      uv2.x += v.x*q1 + v.y*q2;
      uv2.y += v.x*q2 - v.y*q1;

    // sample previous frame
    ret = texture( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;    
}

comp:
shader_body
{
ret = mix(((texture(sampler_blur2, uv).xyz * scale2) + bias2),texture(sampler_main, uv).xyz,0.4);

ret = (ret.y-ret.x*0.5)*mix(vec3(1.8,2.3,1.1),1,ret.x)*1.4;
ret *= 1.99; //MilkDrop2077 gamma effect


ret *= vec3(rad*3,1,1);
}

warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 t = 0.005 + 0.045*saturate(float2(bass,treb)-1);
    float2 uv2 = uv;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, 
        lerp(uv,uv_orig,-1) + delta2 ).xyz;
    float2 v = (blurry_color.xy-0.37) * t;
      //uv2 += v;
      // instead, add a rotated version:
      uv2.x += v.x*q1 + v.y*q2;
      uv2.y += v.x*q2 - v.y*q1;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;    
}

comp_hlsl:
shader_body
{
ret = lerp(GetBlur2(uv),GetPixel(uv),0.4);

ret = (ret.y-ret.x*0.5)*lerp(float3(1.8,2.3,1.1),1,ret.x)*1.4;
ret *= 1.99; //MilkDrop2077 gamma effect


ret *= float3(rad*3,1,1);
}
