float fadeout   = -0.0015;    // may be minor 1/256 due to the error diffusion dither;

vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw*1.5 + rand_frame.xy;
float magic = lum(texture(sampler_main, mix(uv_orig,uv, 8)).xyz) - lum(((texture(sampler_blur1, mix(uv_orig,uv,-8)).xyz * scale1) + bias1));
uv = mix(uv_orig,uv, magic*0.75 ) + (texture(sampler_noise_lq, dither_uv).xyz-0.5)*texsize.zw*error;
