frame_eqs_eel:
wave_b = 0.5+0.5*sin(time*0.561);
wave_r = 0.5+0.5*sin(time*0.861);
wave_g = 0.5+0.5*sin(time*0.961);
ob_r = 1-wave_r;
ob_g = 1-wave_g;
ob_b = 1-wave_b;

volume = 0.15*(bass_att+bass+mid+mid_att);
beatrate = if(equal(beatrate,0),1,if(below(volume,0.01),1,beatrate));
lastbeat = if(equal(lastbeat,0),time,lastbeat);
meanbass_att = 0.1*(meanbass_att*9 + bass_att);
peakbass_att = if(above(bass_att,peakbass_att),bass_att,peakbass_att);
beat = if(above(volume,0.8),if(below(peakbass_att - bass_att, 0.05*peakbass_att),if(above(time - lastbeat,0.1+0.5*(beatrate-0.1)),1,0),0),0);
beatrate = max(if(beat,if(below(time-lastbeat,2*beatrate),0.1*(beatrate*9 + time - lastbeat),beatrate),beatrate),0.1);
peakbass_att = if(equal(beat,0),if(above(time - lastbeat,2*beatrate),peakbass_att*0.95,peakbass_att*0.995),bass_att);
lastbeat = if(beat,time,lastbeat);
countertime = if(beat,time,countertime);
counter =-1*pow(min((time-countertime-3),0),3);
counter2 =-1*pow(min((time-countertime-2.7),0),5);
q1 = 0.35*min(time-countertime,1);
q4=oldq4+0.001*counter2;
oldq4 =q4;
q3=oldq3+0.005*counter;
oldq3 =q3;
monitor = q3;

pixel_eqs_eel:
