frame_eqs_eel:
dec_m = pow(.9, 30/fps);dec_s = pow(.98, 30/fps);dec_xs = pow(.985, 30/fps);

flen = 128; shapes = 16;

beat = (bass + treb + mid)/3;
beat_ = beat_*dec_m + (1-dec_m)*beat; 
is_beat = above(beat, peak*8) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_m;
index = (index + is_beat) %8;
q22 = peak;
q27 = index;

n = 0; vol = .5; bbase = 5000;
loop (flen,
  value = gmegabuf(n) * (1+1/(n+.5));
  trig = value >= megabuf(n);
  dec = if(trig,0.2,pow(.98-.1*n/flen,30/fps));
  megabuf(n) = megabuf(n)*dec + (1-dec)*value;
  vol = vol + pow(megabuf(n),2);
  n = n+1;
);
vol = sqrt(vol);
vol_ = dec_m*vol_ + (1-dec_m)*vol;

// post smoothing
n = 0;
loop (flen,
  value = megabuf(n);
  dec = if (megabuf(1000+n)<=value,.9,.95-0.1*n/flen);
  dec = pow(dec,30/fps);
//  dec = 1; //#
    megabuf(n+1000) = megabuf(n+1000)*dec+ (1-dec)*value/vol_;
  n = n+1;
);


// Berechnung Ortskoordinaten aus Spektrum
