warp:
shader_body
{
uv = mix(uv_orig,uv,0.3);

vec2 d = texsize.zw * 3;
vec3 dx = ( ((texture(sampler_blur1, uv + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
vec3 dy = ( ((texture(sampler_blur1, uv + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );

uv += vec2(lum(dx),lum(dy))*texsize.zw*9;
ret =tex2d(sampler_fc_main, uv) - 0.01;

}

comp:
shader_body
{

//uv = (uv-.5) * aspect.xy + .5;


vec2 hor = vec2 (texsize.z,0)*2;
vec2 ver = vec2 (0,texsize.w)*2;
float dx = ((texture(sampler_blur1, uv-hor).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv+hor).xyz * scale1) + bias1);
float dy = ((texture(sampler_blur1, uv-ver).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv+ver).xyz * scale1) + bias1);
vec2 dz = vec2 (dx,dy)*0.5;



vec3 crisp = texture(sampler_main, uv + dz).xyz * 
               texture(sampler_main, 1-uv - dz).xyz;
vec3 blur = ((texture(sampler_blur1, fract(uv + dz)).xyz * scale1) + bias1);

vec2 uv1 = uv-.5 + dz/4;
vec2 uv2 = uv1*0+.2*vec2 (cos(uv1*4+time));;
vec3 dots = clamp(.01/(length(uv2)), 0.0, 1.0) * vec3 (1,1,1);


ret =(crisp + blur*2) + q21*dots * (1-blur);
ret *= .7 ;
}

