comp:
shader_body
{
    ret =texture(sampler_main, (uv-0.5)*0.6+0.5).zzz;

    float crisp, blur;
    vec2 uv2;

    float shadow_offset = 0.0;
    float shadow_str = 1.15;

    uv2 = (uv-0.5)*0.8+0.5;
    crisp = texture(sampler_main, uv2).y;
      // add drop shadow:
      blur  = ((texture(sampler_blur1, uv2 + shadow_offset).xyz * scale1) + bias1).y;
      ret *= (1-blur*shadow_str);
    ret =max(ret, crisp.xxx);

    uv2 = uv;
    crisp = texture(sampler_main, uv2).x;
      // add drop shadow:
      blur  = ((texture(sampler_blur1, uv2 + shadow_offset).xyz * scale1) + bias1).x;
      ret *= (1-blur*shadow_str);
    ret =max(ret, crisp.xxx);    
}

