wave_r = wave_r + 1.3*cos(vol*150);
wave_b = wave_b + 0.3*sin(vol*20);
wave_g = wave_g + 0.5*tan(vol*35);


q8=wave_r;
q7=wave_b;
q6=wave_g;

wr = 0.5+0.4*(0.6*sin(time*1.1) + 0.4*sin(0.8*time));
wb = 0.5+0.4*(0.6*sin(time*1.6) + 0.4*sin(0.5*time));
wg = 0.5+0.4*(0.6*sin(time*1.34) + 0.4*sin(0.4*time));

monitor=wg;
q10=wr;
q11=wb;
q12=wg;
q18 = 0.007*sin(time*0.1);
q17 = -0.007*sin(time*0.254);
q2=bass_thresh;

vol=(bass+mid+treb)*0.25;
vol=vol*vol;
q3=vol;
rot = 0.011;
warp=0;

warp:
shader_body
{


    vec3 rc = texture(sampler_main, uv).xyz+((texture(sampler_blur1, uv).xyz * scale1) + bias1);//+texture(sampler_main, uv).xyz;
    uv = uv-vec2(0,1);
    vec2 ruv = 0.5 + (uv-0.5)*(1+(rc.y*0.02));  

    ruv = fract(vec2(ruv.x+pow(rc.x,0), ruv.y+pow(rc.x, 0.004)));

    vec3 noise3 = texture(sampler_noise_lq, uv_orig*texsize.xy*texsize_noise_lq.zw+rand_frame.xy).xyz;
    noise3 *= fract(q15);
    noise3 = mix(noise3, vec3(0,0,0), fract(q3*bass));

    ret = texture(sampler_main, vec2(ruv.x, ruv.y)).xyz;

    vec3 noise2 = mix(noise3,vec3(q10,q11,q12), ret.x);    

    ret += clamp(cross((ret), (noise2)), 0.0, 1.0);
    ret *= 0.985; 


}

comp:
shader_body
{
    ret = texture(sampler_main, uv).xyz;
    //ret += ((texture(sampler_blur1, uv).xyz * scale1) + bias1);
    ret += ((texture(sampler_blur2, uv).xyz * scale2) + bias2);
    ret = ret;

    ret *= 1.00; //gamma
}

warp_hlsl:
shader_body
{


    float3 rc = GetPixel(uv)+GetBlur1(uv);//+GetPixel(uv);
    uv = uv-float2(0,1);
    float2 ruv = 0.5 + (uv-0.5)*(1+(rc.y*0.02));  

    ruv = frac(float2(ruv.x+pow(rc.x,0), ruv.y+pow(rc.x, 0.004)));

    float3 noise3 = tex2D(sampler_noise_lq, uv_orig*texsize.xy*texsize_noise_lq.zw+rand_frame.xy);
    noise3 *= frac(q15);
    noise3 = lerp(noise3, float3(0,0,0), frac(q3*bass));

    ret = tex2D(sampler_main, float2(ruv.x, ruv.y)).xyz;

    float3 noise2 = lerp(noise3,float3(q10,q11,q12), ret.x);    

    ret += saturate(cross((ret), (noise2)));
    ret *= 0.985; 


}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    //ret += GetBlur1(uv);
    ret += GetBlur2(uv);
    ret = ret;

    ret *= 1.00; //gamma
}
