warp:
shader_body
{


    vec3 rc = texture(sampler_main, uv).xyz+((texture(sampler_blur1, uv).xyz * scale1) + bias1);//+texture(sampler_main, uv).xyz;
    uv = uv-vec2(0,1);
    vec2 ruv = 0.5 + (uv-0.5)*(1+(rc.y*0.03));  

    ruv = fract(vec2(ruv.x+pow(rc.x,0), ruv.y+pow(rc.x, 0.005)));

    vec3 noise3 = texture(sampler_noise_lq, uv_orig*texsize.xy*texsize_noise_lq.zw+rand_frame.xy).xyz;
    noise3 *= fract(q15);
    noise3 = mix(noise3, vec3(0,0,0), fract(q3*0.5));

    ret = texture(sampler_main, vec2(ruv.x, ruv.y)).xyz;

    vec3 noise2 = mix(noise3,vec3(q10,q11,q12), ret.x);    

    ret += clamp(cross((ret), (noise2)), 0.0, 1.0);
    ret *= 1; 


}

comp:
shader_body
{

ret = texture(sampler_main, uv).xyz;
ret = ret*1.5 - 0.1;

vec4 g;
vec3 d = vec3(2.5,1.3,1.2); //MilkDrop2077 color mod
g.x = lum(((texture(sampler_blur1, uv + d.xz).xyz * scale1) + bias1));
g.y = lum(((texture(sampler_blur1, uv - d.xz).xyz * scale1) + bias1));
g.z = lum(((texture(sampler_blur1, uv + d.zy).xyz * scale1) + bias1));
g.w = lum(((texture(sampler_blur1, uv - d.zy).xyz * scale1) + bias1));

ret *= 0.7;
ret.x += clamp(g.x-g.y, 0.0, 1.0)*5;
ret.z += clamp(g.y-g.x, 0.0, 1.0)*5;
    //ret.xy += (g.xz-g.yw)*3;

ret.x = ((texture(sampler_blur1, uv).xyz * scale1) + bias1).x*2;
ret.z = ((texture(sampler_blur2, uv).xyz * scale2) + bias2).z*3;

vec2 uv_echo = (uv - 0.5)*vec2(-1,-1) + 0.5;
vec3 ret2 = texture(sampler_main, uv_echo).xyz; //MilkDrop2077 color mod

ret2 = ret2*1.5 - 0.1;

vec4 h;
vec3 e = vec3(0.5,1.9,1.7); //MilkDrop2077 color mod
h.x = lum(((texture(sampler_blur1, uv_echo + e.xz).xyz * scale1) + bias1));
h.y = lum(((texture(sampler_blur1, uv_echo - e.xz).xyz * scale1) + bias1));
h.z = lum(((texture(sampler_blur1, uv_echo + e.zy).xyz * scale1) + bias1));
h.w = lum(((texture(sampler_blur1, uv_echo - e.zy).xyz * scale1) + bias1));

ret2 *= 0.7;
ret2.x += clamp(h.x-h.y, 0.0, 1.0)*5;
ret2.z += clamp(h.y-h.x, 0.0, 1.0)*5;
//ret.xy += (h.xz-h.yw)*3;

ret2.x = ((texture(sampler_blur1, uv_echo).xyz * scale1) + bias1).x*2;
ret2.z = ((texture(sampler_blur2, uv_echo).xyz * scale2) + bias2).z*3;

ret = mix( ret, ret2, 1 ); //video echo
ret -= 0.11;
//ret *= (hue_shader*8)-8.992;

}

warp_hlsl:
shader_body
{


    float3 rc = GetPixel(uv)+GetBlur1(uv);//+GetPixel(uv);
    uv = uv-float2(0,1);
    float2 ruv = 0.5 + (uv-0.5)*(1+(rc.y*0.03));  

    ruv = frac(float2(ruv.x+pow(rc.x,0), ruv.y+pow(rc.x, 0.005)));

    float3 noise3 = tex2D(sampler_noise_lq, uv_orig*texsize.xy*texsize_noise_lq.zw+rand_frame.xy);
    noise3 *= frac(q15);
    noise3 = lerp(noise3, float3(0,0,0), frac(q3*0.5));

    ret = tex2D(sampler_main, float2(ruv.x, ruv.y)).xyz;

    float3 noise2 = lerp(noise3,float3(q10,q11,q12), ret.x);    

    ret += saturate(cross((ret), (noise2)));
    ret *= 1; 


}

