comp:
uniform sampler2D sampler_pw_noise_lq;
vec3 ret1;
vec2 zz, uv0, uv1,uv2,uv3,uv4;
float smask;

float aTan2(float y, float x) {float angle;	 float abs_y = abs(y);
if (x >= 0) {float r = (x - abs_y) / (x + abs_y); angle = 1-r;} 
else {float r = (x + abs_y) / (abs_y - x); angle = 3-r; }	
angle = angle * .25;
return y < 0 ? -angle : angle;}

shader_body { ;
uv0 = uv;
uv1 = (uv-.5)*aspect.xy;

uv1 = (uv - .5) * aspect.xy;    
float stars = 0; float dist, inten;
int n = 1;
int anz = 4;
while (n <= anz) {
  dist = (1-fract(1.0/anz*n+.10*time)) * (1-.2*rad);
  inten =  (1-dist);
  uv3 = (uv1+vec2(q1,q2));
   uv4 = 64*dist*uv3;
  smask = (texture (sampler_pw_noise_lq,uv4/256).xyz).g-.9;
  stars += max(stars,clamp(inten*(0.11/length(abs(fract(uv4)-0.47))*smask), 0.0, 1.0));
n++;
}
uv2 = uv1 - vec2(q5,q6);
float beat = (mid - 0.45) * (mid > 0.45)*2;
float flash = .01/(length(uv2))*min(3,pow(beat,1));
flash *= flash/(abs(fract(3*aTan2(uv2.x,uv2.y)+time*2)-.5));
ret1 = max(texture(sampler_main, uv0).xyz*2,((texture(sampler_blur2, uv0).xyz * scale2) + bias2)*2);
//ret = clamp(0.022/length(sin(uv3)), 0.0, 1.0)*vec3(0.2,2.6,0.1); //MilkDrop2077 color mod
ret += clamp(pow(stars,2), 0.0, 1.0);
ret *= clamp(1-2*lum(ret1), 0.0, 1.0);
ret +=  ret1;
ret -= 0.1;
//ret += 2*clamp(flash, 0.0, 1.0)*vec3(2.2,2.9,0.7); //MilkDrop2077 color mod
ret /= vec3(2.0,2.9,2.3);
ret*=1.4;
}

